import{_ as e,c as i,o as a,a as l}from"./app.d4eb32af.js";const f='{"title":"\u7A77\u4EBA\u65E0\u6CD5\u6446\u8131\u7A77","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7A77\u4EBA\u65E0\u6CD5\u6446\u8131\u7A77","slug":"\u7A77\u4EBA\u65E0\u6CD5\u6446\u8131\u7A77"},{"level":2,"title":"\u7ED3\u5A5A\u4E0D\u8981\u53BB\u4E70\u94B4\u6212","slug":"\u7ED3\u5A5A\u4E0D\u8981\u53BB\u4E70\u94B4\u6212"},{"level":2,"title":"\u4E0D\u8981\u5BF9\u77E5\u8BC6\u529F\u5229","slug":"\u4E0D\u8981\u5BF9\u77E5\u8BC6\u529F\u5229"},{"level":2,"title":"\u65F6\u95F4\u7BA1\u7406","slug":"\u65F6\u95F4\u7BA1\u7406"}],"relativePath":"economics/index.md"}',t={},r=l('<h2 id="\u7A77\u4EBA\u65E0\u6CD5\u6446\u8131\u7A77" tabindex="-1">\u7A77\u4EBA\u65E0\u6CD5\u6446\u8131\u7A77 <a class="header-anchor" href="#\u7A77\u4EBA\u65E0\u6CD5\u6446\u8131\u7A77" aria-hidden="true">#</a></h2><p>\u6446\u8131\u8D2B\u7A77\u96BE, \u56E0\u4E3A\u662F\u9677\u9631, \u751F\u4E0B\u6765\u5C31\u8BBE\u5B9A\u597D\u7684\u9677\u9631, \u8BA9\u4EBA\u8212\u670D, \u8BA9\u4EBA\u610F\u8BC6\u4E0D\u5230, \u7A77\u4E86\u5C31\u60F3\u7740\u7701\u94B1, \u8D8A\u7701\u94B1\u8D8A\u6CA1\u94B1, \u7A77\u4E86\u5C31\u60F3\u7A33\u5B9A, \u8D8A\u7A33\u5B9A\u8D8A\u6293\u4E0D\u4F4F\u673A\u4F1A</p><p>\u60F3\u6446\u8131\u51FA\u53BB\u7684\u65B9\u6CD5: \u53CD\u4EBA\u6027 \u8212\u670D\u7684, \u4E60\u60EF\u7684, \u4EAB\u53D7\u7684\u5C31\u4E0D\u8981\u505A, \u5728\u4F60\u7684\u8212\u9002\u533A. \u56E0\u4E3A\u5B83\u5728\u4F60\u7684\u4E60\u60EF\u5185, \u4F60\u5C31\u610F\u8BC6\u4E0D\u5230, \u4E0D\u80FD\u6446\u8131 \u516D\u70B9: \u6700\u91CD\u8981\u6700\u540E\u4E00\u70B9</p><ol><li>\u8D8A\u7A77\u8D8A\u7231\u9762\u5B50, \u4E70\u8D35\u7684\u4E1C\u897F\u5145\u95E8\u9762, \u628A\u9762\u5B50\u653E\u5230\u4E00\u8FB9, \u4E0D\u8981\u4E3A\u9762\u5B50\u4ED8\u8D39, \u53EA\u6709\u4F60\u77E5\u9053, \u4F60\u7684\u76EE\u6807\u662F\u4EC0\u4E48, \u4F60\u53EF\u4EE5\u4ED8\u51FA\u591A\u5927\u4EE3\u4EF7, \u4F60\u53EF\u4EE5\u4E3A\u4E86\u76EE\u6807\u653E\u5F03\u4EC0\u4E48</li><li>\u6295\u8D44\u548C\u6D88\u8D39\u662F\u76F8\u4E92\u77DB\u76FE\u7684, \u6D88\u8D39\u8D8A\u591A, \u7559\u7ED9\u6295\u8D44\u7684\u8D8A\u5C11, \u8D8A\u662F\u5403\u559D\u73A9\u4E50, \u8D8A\u6CA1\u94B1\u6295\u8D44\u81EA\u5DF1, \u5C31\u8D8A\u9003\u4E0D\u51FA\u8FD9\u4E2A\u9677\u9631, \u8D8A\u7A77\u5C31\u8981\u514B\u5236\u8FD9\u79CD\u6B32\u671B\u628A\u6D88\u8D39\u538B\u7F29\u5230\u6700\u4F4E, \u624D\u6709\u94B1\u6295\u8D44, \u7528\u5728\u672A\u6765.</li><li>\u820D\u5F97\u4E3A\u77E5\u8BC6\u4ED8\u8D39, \u6295\u8D44\u4E0D\u662F\u7406\u8D22\u800C\u662F\u6295\u8D44\u81EA\u5DF1, \u6700\u597D\u7684\u56DE\u62A5\u662F\u77E5\u8BC6, \u628A\u5403\u559D\u73A9\u4E50\u7701\u4E0B\u7684\u94B1\u7528\u5728\u4E70\u4E66, \u5B66\u4E60\u65B9\u9762</li><li>\u8BE5\u8E29\u5751\u5C31\u8E29\u5751, \u7A77\u7684\u65F6\u5019\u4E0D\u8E29\u5751, \u540E\u9762\u8E29\u5751\u4ED8\u51FA\u7684\u4EE3\u4EF7\u5C31\u66F4\u5927, \u5403\u4E8F\u8D8A\u65E9\u8BD5\u9519\u6210\u672C\u5C31\u8D8A\u5C0F</li><li>\u5BFB\u627E\u4E0D\u786E\u5B9A\u6027, \u8D8A\u7A77\u8D8A\u5B89\u4E8E\u73B0\u72B6, \u8D8A\u6CA1\u6CD5\u7A81\u7834</li><li>\u5207\u6362\u73AF\u5883\u53BB\u66F4\u5927\u7684\u57CE\u5E02, \u53BB\u9876\u5C16\u4EBA\u624D\u6700\u591A\u7684\u5730\u65B9. \u53BB\u60F3\u53BB\u4F46\u4ECE\u6765\u6CA1\u53BB\u7684\u5730\u65B9.\u53BB\u7ADE\u4E89\u6700\u6FC0\u70C8\u7684\u5730\u65B9.</li></ol><h2 id="\u7ED3\u5A5A\u4E0D\u8981\u53BB\u4E70\u94B4\u6212" tabindex="-1">\u7ED3\u5A5A\u4E0D\u8981\u53BB\u4E70\u94B4\u6212 <a class="header-anchor" href="#\u7ED3\u5A5A\u4E0D\u8981\u53BB\u4E70\u94B4\u6212" aria-hidden="true">#</a></h2><p>\u94BB\u6212\u6D41\u901A\u6027\u5DEE, \u4E0D\u662F\u6295\u8D44\u7406\u8D22\u4EA7\u54C1, \u8D2D\u4E70\u4F1A\u4F24\u5BB3\u672C\u91D1.</p><h2 id="\u4E0D\u8981\u5BF9\u77E5\u8BC6\u529F\u5229" tabindex="-1">\u4E0D\u8981\u5BF9\u77E5\u8BC6\u529F\u5229 <a class="header-anchor" href="#\u4E0D\u8981\u5BF9\u77E5\u8BC6\u529F\u5229" aria-hidden="true">#</a></h2><p>\u8981\u5E7F\u6CDB\u6444\u53D6, \u6700\u7EC8\u4F60\u4F1A\u53D1\u73B0, \u4EE5\u524D\u968F\u610F\u6444\u53D6\u7684\u77E5\u8BC6\u90FD\u53EF\u4EE5\u878D\u6C47\u4E00\u8D77.</p><h2 id="\u65F6\u95F4\u7BA1\u7406" tabindex="-1">\u65F6\u95F4\u7BA1\u7406 <a class="header-anchor" href="#\u65F6\u95F4\u7BA1\u7406" aria-hidden="true">#</a></h2><ol><li>\u5F53\u4F60\u4E0D\u9700\u8981\u7528\u5230\u5927\u8111\u7684\u65F6\u5019\uFF0C\u4F60\u624D\u9700\u8981\u65F6\u95F4\u7BA1\u7406</li><li>\u4E0A\u5347\u5230\u7EDF\u7B79\u73AF\u8282\u65F6\u624D\u53D1\u73B0\u65F6\u95F4\u4E0D\u591F\u7528\u3002</li><li>\u4EBA\u751F\u6700\u5927\u6743\u91CD\u662F\u7B2C\u96F6\u6B65\uFF0C\u6743\u91CD\uFF0C\u7B56\u7565\uFF0C\u65B9\u5411\uFF0C\u53D6\u820D\u3002</li><li>\u65F6\u95F4\u7BA1\u7406\u7684\u771F\u6B63\u5173\u952E\u662F\u627E\u51FA\u6700\u5927\u6743\u91CD\uFF0C\u7136\u540E\u4E0D\u8BA1\u6210\u672C\u7684\u6295\u5165\uFF0C\u628A\u65F6\u95F4\u653E\u5728\u51B3\u7B56\u4E0A\u3002\u53EA\u505A\u6700\u91CD\u8981\u7684\u90A3\u4E00\u4EF6\u4E8B\u513F\u3002</li><li>\u65F6\u95F4\u4E0D\u9760\u7BA1\u7406\uFF0C\u9760\u521B\u9020</li></ol>',10),d=[r];function s(n,o,h,c,_,p){return a(),i("div",null,d)}var x=e(t,[["render",s]]);export{f as __pageData,x as default};
