import{_ as l,c as i,o as e,a as t}from"./app.d4eb32af.js";const f='{"title":"\u4E0A\u73ED\u65CF\u5E94\u77E5\u7684\u52B3\u52A8\u6CD5","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E0A\u73ED\u65CF\u5E94\u77E5\u7684\u52B3\u52A8\u6CD5","slug":"\u4E0A\u73ED\u65CF\u5E94\u77E5\u7684\u52B3\u52A8\u6CD5"},{"level":2,"title":"\u52B3\u52A8\u6CD5\u89E3\u8BFB","slug":"\u52B3\u52A8\u6CD5\u89E3\u8BFB"},{"level":2,"title":"\u5E94\u77E5\u7684\u52B3\u52A8\u5408\u540C\u6CD5","slug":"\u5E94\u77E5\u7684\u52B3\u52A8\u5408\u540C\u6CD5"}],"relativePath":"law/\u52B3\u52A8\u6CD5.md"}',a={},r=t(`<p><a href="https://gkml.samr.gov.cn/nsjg/bgt/202106/t20210610_330502.html" target="_blank" rel="noopener noreferrer">\u52B3\u52A8\u6CD5</a></p><h2 id="\u4E0A\u73ED\u65CF\u5E94\u77E5\u7684\u52B3\u52A8\u6CD5" tabindex="-1">\u4E0A\u73ED\u65CF\u5E94\u77E5\u7684\u52B3\u52A8\u6CD5 <a class="header-anchor" href="#\u4E0A\u73ED\u65CF\u5E94\u77E5\u7684\u52B3\u52A8\u6CD5" aria-hidden="true">#</a></h2><ol><li><p>\u8BD5\u7528\u671F\u4E0D\u4E70\u793E\u4FDD</p></li><li><p>\u516C\u53F8\u4E0D\u7B7E\u5408\u540C(\u5305\u62EC\u8BD5\u7528\u671F)</p></li><li><p>\u8BD5\u7528\u671F\u5EF6\u957F</p></li><li><p>\u52A0\u73ED\u53EF\u4EE5\u8C03\u4F11\u4E0D\u7ED9\u52A0\u73ED\u8D39\u5408\u6CD5\u5417</p><pre><code>\u4F46\u662F\u5E73\u65F6\u5DE5\u4F5C\u65E5\u5EF6\u957F\u65F6\u95F4\u52A0\u73ED\u7684\uFF0C\u52B3\u52A8\u6CD5\u5E76\u672A\u89C4\u5B9A\u53EF\u4EE5\u4F18\u5148\u5B89\u6392\u8C03\u4F11\uFF0C\u636E\u6B64\uFF0C\u5DE5\u4F5C\u65E5\u52A0\u73ED\u7684\u662F\u4E0D\u80FD\u4EE5\u8C03\u4F11\u6765\u4EE3\u66FF\u52A0\u73ED\u8D39\uFF0C\u516C\u53F8\u5728\u5DE5\u4F5C\u65E5\u5B89\u6392\u4E86\u52A0\u73ED\uFF0C\u5C31\u9700\u8981\u5411\u5458\u5DE5\u652F\u4ED8 1.5 \u500D\u5DE5\u8D44\u3002

\u5177\u4F53\u800C\u8A00\uFF0C\u53EA\u8C03\u4F11\u800C\u4E0D\u7ED9\u52A0\u73ED\u8D39\u662F\u5426\u53EF\u4EE5\uFF0C\u5206\u4EE5\u4E0B\u51E0\u79CD\u60C5\u51B5\u6765\u5B9A\uFF1A

1\u3001\u6B63\u5E38\u5DE5\u4F5C\u65E5\u5EF6\u957F\u5DE5\u4F5C\u65F6\u95F4\u8FDB\u884C\u52A0\u73ED\u7684\uFF0C\u5E94\u5F53\u652F\u4ED8\u4E0D\u4F4E\u4E8E\u5DE5\u8D44\u7684 150%\u7684\u5DE5\u8D44\u62A5\u916C\u3002\u8BE5\u52A0\u73ED\u65F6\u95F4\u4E0D\u53EF\u4EE5\u7528\u8C03\u4F11\u5904\u7406\uFF0C\u53EA\u80FD\u652F\u4ED8\u76F8\u5E94\u7684\u5DE5\u8D44\u62A5\u916C\u3002

2\u3001\u5B89\u6392\u5468\u672B\u52A0\u73ED\u7684\uFF0C\u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u5B89\u6392\u8C03\u4F11\u4EE3\u66FF\u3002\u4F46\u5982\u679C\u7528\u4EBA\u5355\u4F4D\u65E0\u6CD5\u5B89\u6392\u8C03\u4F11\u7684\uFF0C\u5E94\u5F53\u652F\u4ED8\u4E0D\u4F4E\u4E8E\u5DE5\u8D44\u7684 200%\u7684\u5DE5\u8D44\u62A5\u916C\u3002

3\u3001\u6CD5\u5B9A\u8282\u5047\u65E5\u52A0\u73ED\u7684\uFF0C\u8BE5\u65F6\u6BB5\u7684\u52A0\u73ED\u5373\u4F7F\u540E\u671F\u5B89\u6392\u5458\u5DE5\u8C03\u4F11\uFF0C\u7528\u4EBA\u5355\u4F4D\u4E5F\u5E94\u652F\u4ED8\u4E0D\u4F4E\u4E8E\u5DE5\u8D44\u7684 300\uFF05\u7684\u5DE5\u8D44\u62A5\u916C\u3002
</code></pre></li><li><p>\u7B7E\u5408\u540C\u6CE8\u610F\u7ADE\u4E1A\u9650\u5236</p></li></ol><p>\u5728\u7B7E\u8BA2\u7ADE\u4E1A\u9650\u5236\u671F\u95F4\uFF0C\u5982\u679C\u8FDD\u53CD\uFF0C\u8981\u627F\u62C5\u8FDD\u7EA6\u8D23\u4EFB\u3002\u5728\u8FD9\u4E24\u5E74\u65F6\u95F4\u4E2D\uFF0C\u516C\u53F8\u4F1A\u652F\u4ED8\u4E00\u5B9A\u7684\u8865\u507F\u7ED9\u4F60\u3002\u5BF9\u4E8E\u5E94\u652F\u4ED8\u591A\u5C11\u7684\u8865\u507F\u8D39\u7528\uFF0C\u6839\u636E\u6BCF\u4E2A\u52B3\u52A8\u8005\u548C\u7528\u4EBA\u5355\u4F4D\u60C5\u51B5\u4E0D\u540C\u800C\u4E0D\u540C\uFF0C\u6CD5\u5F8B\u4E0A\u4E5F\u6CA1\u6709\u660E\u786E\u7684\u6570\u989D\u6807\u51C6. \u5982\u679C\u6CA1\u6709\u652F\u4ED8\u7ADE\u4E1A\u8865\u507F\u91D1\u989D, \u5C06\u4E0D\u751F\u6548, \u4E00\u822C\u53EF\u4EE5\u6309\u7167\u4E0A\u4E00\u5E74\u5EA6\u5E74\u6536\u5165\u7684 1/2 \u6216\u8005 2/3</p><ol start="6"><li>\u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u5F00\u9664\u5458\u5DE5\u7684\u60C5\u51B5</li></ol><p>\u7B2C\u4E8C\u5341\u4E94\u6761 \u52B3\u52A8\u8005\u6709\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u89E3\u9664\u52B3\u52A8\u5408\u540C\uFF1A \uFF08\u4E00\uFF09\u5728\u8BD5\u7528\u671F\u95F4\u88AB\u8BC1\u660E\u4E0D\u7B26\u5408\u5F55\u7528\u6761\u4EF6\u7684\uFF1B \uFF08\u4E8C\uFF09\u4E25\u91CD\u8FDD\u53CD\u52B3\u52A8\u7EAA\u5F8B\u6216\u8005\u7528\u4EBA\u5355\u4F4D\u89C4\u7AE0\u5236\u5EA6\u7684\uFF1B \uFF08\u4E09\uFF09\u4E25\u91CD\u5931\u804C\uFF0C\u8425\u79C1\u821E\u5F0A\uFF0C\u5BF9\u7528\u4EBA\u5355\u4F4D\u5229\u76CA\u9020\u6210\u91CD\u5927\u635F\u5BB3\u7684\uFF1B \uFF08\u56DB\uFF09\u88AB\u4F9D\u6CD5\u8FFD\u7A76\u5211\u4E8B\u8D23\u4EFB\u7684\u3002</p><h2 id="\u52B3\u52A8\u6CD5\u89E3\u8BFB" tabindex="-1">\u52B3\u52A8\u6CD5\u89E3\u8BFB <a class="header-anchor" href="#\u52B3\u52A8\u6CD5\u89E3\u8BFB" aria-hidden="true">#</a></h2><p>\u5C06\u548C\u6211\u4EEC\u606F\u606F\u76F8\u5173\u7684\u5217\u51FA, \u5E76\u6807\u6CE8\u51FA\u81F3\u52B3\u52A8\u6CD5\u7684\u54EA\u4E00\u4E2A\u5730\u65B9</p><ol><li>\u5987\u5973\u548C\u7537\u5B50\u4EAB\u53D7\u5E73\u7B49\u7684\u5C31\u4E1A\u6743\u9650 (\u7B2C\u5341\u4E09\u6761)</li><li>\u52B3\u52A8\u5408\u540C\u5E94\u8BE5\u5305\u542B\u52B3\u52A8\u5408\u540C\u671F\u9650,\u5DE5\u4F5C\u5185\u5BB9,\u52B3\u52A8\u4FDD\u62A4\u548C\u52B3\u52A8\u6761\u4EF6,\u52B3\u52A8\u62A5\u916C,\u52B3\u52A8\u7EAA\u5F8B,\u52B3\u52A8\u5408\u540C\u7EC8\u6B62\u7684\u6761\u4EF6,\u8FDD\u53CD\u52B3\u52A8\u5408\u540C\u7684\u8D23\u4EFB\u3002 (\u7B2C\u5341\u4E5D\u6761)</li><li>\u8BD5\u7528\u671F\u4E0D\u5F97\u591A\u4F59 6 \u4E2A\u6708 (\u7B2C\u4E8C\u5341\u4E00\u6761)</li><li>\u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u89E3\u9664\u52B3\u52A8\u5408\u540C\u60C5\u51B5 (\u7B2C\u4E8C\u5341\u4E94\u6761)</li><li>\u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u89E3\u9664\u52B3\u52A8\u5408\u540C\u4F46\u9700\u63D0\u524D 30 \u65E5\u63D0\u51FA\u4E66\u9762\u7533\u8BF7 (\u7B2C\u4E8C\u5341\u516D\u6761)</li><li>\u5728\u8BD5\u7528\u671F\u95F4\u88AB\u8BC1\u660E\u4E0D\u7B26\u5408\u5F55\u7528\u6761\u4EF6\u7684, \u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u89E3\u9664\u5408\u540C, \u5E76\u4E0D\u8D54\u507F</li><li>\u60A3\u804C\u4E1A\u75C5\u6216\u8005\u56E0\u5DE5\u8D1F\u4F24, \u5973\u804C\u5DE5\u5728\u5B55\u671F\u3001\u4EA7\u671F\u3001\u54FA\u4E73\u671F\u5185\u7684, \u7528\u4EBA\u5355\u4F4D\u4E0D\u80FD\u5F00\u9664\u4EBA (\u7B2C\u4E8C\u5341\u4E5D\u6761)</li><li>\u52B3\u52A8\u8005\u89E3\u9664\u52B3\u52A8\u5408\u540C 30 \u5929\u63D0\u51FA, \u8BD5\u7528\u671F 3 \u5929 (\u7B2C\u4E09\u5341\u4E00\u6761)</li><li>\u5728\u8BD5\u7528\u671F\u5185, ,\u7528\u4EBA\u5355\u4F4D\u672A\u6309\u7167\u52B3\u52A8\u5408\u540C\u7EA6\u5B9A\u652F\u4ED8\u52B3\u52A8\u62A5\u916C\u6216\u8005\u63D0\u4F9B\u52B3\u52A8\u6761\u4EF6, \u52B3\u52A8\u8005\u53EF\u4EE5\u968F\u65F6\u63D0\u51FA\u89E3\u9664\u5408\u540C (\u7B2C\u4E09\u5341\u4E8C\u6761)</li><li>\u6BCF\u65E5\u5DE5\u4F5C\u65F6\u957F\u4E0D\u8D85\u8FC7 8 \u5C0F\u65F6, \u5468\u4E0D\u8D85\u8FC7 44h (\u7B2C\u4E09\u5341\u516D\u6761)</li><li>\u52A0\u73ED, \u5DE5\u4F5C\u65F6\u95F4\u6BCF\u65E5\u4E0D\u5F97\u8D85\u8FC7 3h\uFF0C\u6BCF\u6708\u4E0D\u5F97\u8D85\u8FC7 36h (\u7B2C\u56DB\u5341\u4E00\u6761)</li><li>\u52A0\u73ED\u8D39, \u5DE5\u4F5C\u65E5\u52A0\u73ED 1.5 \u52A0\u73ED\u8D39, \u4F11\u606F\u65E5\u53C8\u4E0D\u80FD\u5B89\u6392\u8865\u4F11\u7684 2 \u500D, \u6CD5\u5B9A\u8282\u5047\u65E5 3 \u500D (\u7B2C\u56DB\u5341\u56DB\u6761)</li><li>\u4E00\u5E74\u4EE5\u4E0A\u6709\u5E26\u85AA\u5E74\u5047 (\u7B2C\u56DB\u5341\u4E94\u6761)</li><li>\u4E0D\u5F97\u968F\u610F\u6263\u5DE5\u8D44 (\u7B2C\u4E94\u5341\u6761)</li><li>\u4EA7\u5047\u4E0D\u5F97\u5C11\u4E8E 90 \u5929 (\u7B2C\u516D\u5341\u4E8C\u6761)</li><li>\u7528\u4EBA\u5355\u4F4D\u4E0D\u7F34\u7EB3\u793E\u4F1A\u4FDD\u9669, \u9650\u671F\u7F34\u7EB3, \u903E\u671F\u6EDE\u7EB3\u91D1 (\u7B2C\u4E00\u767E\u6761)</li></ol><p><a href="http://www.gov.cn/jrzg/2007-06/29/content_667720.htm" target="_blank" rel="noopener noreferrer">\u52B3\u52A8\u5408\u540C\u6CD5</a></p><h2 id="\u5E94\u77E5\u7684\u52B3\u52A8\u5408\u540C\u6CD5" tabindex="-1">\u5E94\u77E5\u7684\u52B3\u52A8\u5408\u540C\u6CD5 <a class="header-anchor" href="#\u5E94\u77E5\u7684\u52B3\u52A8\u5408\u540C\u6CD5" aria-hidden="true">#</a></h2><p>\u52B3\u52A8\u5408\u540C\u6587\u672C\u7531\u7528\u4EBA\u5355\u4F4D\u548C\u52B3\u52A8\u8005\u5404\u6267\u4E00\u4EFD\u3002</p><ol><li>\u8BD5\u7528\u671F\u957F\u77ED (\u7B2C\u5341\u4E5D\u6761)</li></ol><p>3 \u6708\u5230 1 \u5E74 \u8BD5\u7528\u671F 1 \u4E2A\u6708; 1 \u5230 3 \u5E74, \u8BD5\u7528\u671F 3 \u4E2A\u6708; 3 \u5E74\u4EE5\u4E0A 6 \u4E2A\u6708</p><p>\u540C\u4E00\u7528\u4EBA\u5355\u4F4D\u4E0E\u540C\u4E00\u52B3\u52A8\u8005\u53EA\u80FD\u7EA6\u5B9A\u4E00\u6B21\u8BD5\u7528\u671F\u3002</p><ol start="2"><li><p>\u85AA\u8D44\u4E0D\u4F4E\u4E8E 80% (\u7B2C\u4E8C\u5341\u6761)</p></li><li><p>\u4E0D\u5F97\u5F3A\u8FEB\u6216\u53D8\u76F8\u5F3A\u8FEB\u52A0\u73ED (\u7B2C\u4E09\u5341\u4E00\u6761)</p></li><li><p>\u8F9E\u804C, \u52B3\u52A8\u8005\u63D0\u524D\u4E09\u5341\u65E5\u4EE5\u4E66\u9762\u5F62\u5F0F\u901A\u77E5\u7528\u4EBA\u5355\u4F4D\uFF0C\u5728\u8BD5\u7528\u671F\u5185\u63D0\u524D\u4E09\u65E5\u901A\u77E5\u7528\u4EBA\u5355\u4F4D (\u7B2C\u4E09\u5341\u4E03\u6761)</p></li><li><p>\u52B3\u52A8\u8005\u53EF\u4EE5\u89E3\u9664\u52B3\u52A8\u5408\u540C (\u7B2C\u4E09\u5341\u516B\u6761)</p></li></ol><ul><li>\u672A\u53CA\u65F6\u8DB3\u989D\u652F\u4ED8\u52B3\u52A8\u62A5\u916C\u7684</li><li>\u672A\u4F9D\u6CD5\u4E3A\u52B3\u52A8\u8005\u7F34\u7EB3\u793E\u4F1A\u4FDD\u9669\u8D39\u7684</li></ul><ol start="6"><li>\u7528\u4EBA\u5355\u4F4D\u53EF\u4EE5\u89E3\u9664\u52B3\u52A8\u5408\u540C (\u7B2C\u4E09\u5341\u4E5D\u6761)</li></ol><ul><li>\u5728\u8BD5\u7528\u671F\u95F4\u88AB\u8BC1\u660E\u4E0D\u7B26\u5408\u5F55\u7528\u6761\u4EF6\u7684\uFF1B</li><li>\u4E25\u91CD\u8FDD\u53CD\u7528\u4EBA\u5355\u4F4D\u7684\u89C4\u7AE0\u5236\u5EA6\u7684</li><li>\u4E25\u91CD\u5931\u804C, \u7ED9\u7528\u4EBA\u5355\u4F4D\u9020\u6210\u91CD\u5927\u635F\u5BB3\u7684</li></ul><ol start="7"><li><p>\u60A3\u75C5, \u52B3\u52A8\u8005\u4E0D\u80FD\u80DC\u4EFB, \u516C\u53F8\u9700\u4E09\u5341\u65E5\u4E66\u9762\u901A\u77E5\u52B3\u52A8\u8005, \u6216\u8005\u989D\u5916\u652F\u4ED8\u52B3\u52A8\u8005\u4E00\u4E2A\u6708\u5DE5\u8D44 (\u7B2C\u56DB\u5341\u6761)</p></li><li><p>\u60A3\u75C5\u6216\u8005\u975E\u56E0\u5DE5\u8D1F\u4F24\u5728\u89C4\u5B9A\u7684\u533B\u7597\u671F\u5185, \u5973\u804C\u5DE5\u5728\u5B55\u671F\u3001\u4EA7\u671F\u3001\u54FA\u4E73\u671F\u7684 \u4E0D\u5F97\u5F00\u9664 (\u7B2C\u56DB\u5341\u4E8C\u6761)</p></li><li><p>\u53EF\u4EE5\u7533\u8BF7\u8D54\u507F\u7684\u60C5\u51B5 (\u7B2C\u56DB\u5341\u516D\u6761)</p></li></ol>`,20),p=[r];function o(n,s,_,h,c,d){return e(),i("div",null,p)}var g=l(a,[["render",o]]);export{f as __pageData,g as default};
